function printYear($year, $data) {
    $text = "  new Year($year, [\n";    
    foreach ($data as $type => $agegroups) {
        foreach ($agegroups as $agegroup) {
            $text .= printAgegroup($type, $agegroup);
        }
    }
    $text .= "  ]),\n";    
    return $text;
}

function printAgegroup($type, $data) {
    $name = $data[0];
    if ($type == "e") {
        $type = "Types.individual";
    } else {
        $type = "Types.team";
    }
    $amount = $data[1];
    if ($amount < 0) {
        $amount = count($data[2]);
    }
    $text = "    new Agegroup($type, \"$name\", $amount, [\n";
    for ($x = 0; $x < count($data[2]); $x++) {
        $text .= printDiscipline($data[4][$x],$data[2][$x],$data[3][$x]);
    }
    $text .= "    ]),\n";    
    return $text;
}

function printDiscipline($name, $recW, $recM) {
    $name = str_replace('ü', 'ü', $name);
    $name = str_replace('k. Schwimmen', 'Kombiniertes Schwimmen', $name);
    if ($name == '50m Hindernis') {
        $name = '50m Hindernisschwimmen';
    }
    if ($name == '100m Hindernis') {
        $name = '100m Hindernisschwimmen';
    }
    if ($name == '200m Hindernis') {
        $name = '200m Hindernisschwimmen';
    }
    if ($name == '50m Flossen') {
        $name = '50m Flossenschwimmen';
    }

    $text = "      new Discipline(\"$name\", [\n";
    $text .= "        new Record(Sexes.female, $recW),\n";
    $text .= "        new Record(Sexes.male, $recM),\n";
    $text .= "      ]),\n";
    return $text;
}

